package service

import "testing"

func Test_initMap(t *testing.T) {
	tests := []struct {
		name string
	}{
		{"t"},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			InitMap()
		})
	}
}

func TestInitChess(t *testing.T) {
	tests := []struct {
		name string
		want string
	}{
		{"t", "黑车,00,黑马,01,象,02,士,03,将,04,士,05,象,06,黑马,07,黑车,08,黑炮,21,黑炮,27,卒,30,卒,32,卒,34,卒,36,卒,38,兵,60,兵,62,兵,64,兵,66,兵,68,炮,71,炮,77,车,90,马,91,相,92,仕,93,帅,94,仕,95,相,96,马,97,车,98,"},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := InitChess(); got != tt.want {
				t.Errorf("InitChess() = %v, want %v", got, tt.want)
			}
		})
	}
}

func TestIsAbleToMove(t *testing.T) {
	tests := []struct {
		name string
		x    int
		y    int
		tx   int
		ty   int
		want bool
	}{
		{"t", 0, 0, 0, 1, false},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := IsAbleToMove(tt.x, tt.y, tt.tx, tt.ty); got != tt.want {
				t.Errorf("IsAbleToMove() = %v, want %v", got, tt.want)
			}
		})
	}
}

